import React, {Component} from 'react'
import {withRouter, Link} from "react-router-dom";
import {Menu, Dropdown, Button, Icon} from "semantic-ui-react";


class MainLayout extends Component {
    constructor(props) {
        super(props)
        this.state = {
            notificationsCount: 0,
            notifications: [],
            firsCall: true
        }
        let pathName = props ? props.location.pathname.substring(1) : 'first'
        this.state = {
            activeItem: pathName !== 'first' ? pathName : 'home'
        }
    }

    handleItemClick = (e, {name}) => {
        this.setState({
            activeItem: name
        }, () => {
            this.props.history.push(`/${name}`)
        })
    }


    render() {
        const {activeItem, notificationsCount, notifications, firstCall} = this.state
        return (
            <div className={'main-container'}>
                <Menu id={"navbar"} borderless className={"navbar-section"}>
                    <Menu.Item
                        name='demo1'
                        active={activeItem === 'first'}
                        onClick={this.handleItemClick}
                    />
                    <Menu.Item
                        name='demo2'
                        active={activeItem === 'second'}
                        onClick={this.handleItemClick}
                    />

                </Menu>
                <div>
                    {this.props.children}
                </div>
            </div>
        );
    }
}

export default withRouter(MainLayout);
